func TestProposeBlock_BroadcastsBlock(t *testing.T) {
	require.NoError(t, bls.Init(bls.BLS12_381))

	// fixture
	refSk := "5470813f7deef638dc531188ca89e36976d536f680e89849cd9077fd096e20bc"
	//refPk := "a3862121db5914d7272b0b705e6e3c5336b79e316735661873566245207329c30f9a33d4fb5f5857fc6fd0a368186972"
	refDomain := "0000000081509579e35e84020ad8751eca180b44df470332d3ad17fc6fd52459"
	refBlockByts := ""
	refSigByts := "911ac2f6d74039279f16eee4cc46f4c6eea0ef9d18f0d9739b407c150c07ccb104c1c4b034ad46b25719bafc22fad05205975393000ea09636f5ce427814e2fe12ea72041099cc7f6ec249e504992dbf65e968ab448ddf4e124cbcbc722829b5"

	logger := logex.Build("", zap.DebugLevel)
	logger.Info("own DOMAIN", zap.Any("domain", _byteArray(refDomain)))

	b, err := New(context.Background(), logger, "pyrmont", []byte("BloxStaking"), "eth2-4000-prysm-ext.stage.bloxinfra.com:80")
	require.NoError(t, err)
	pk := &bls.SecretKey{}
	require.NoError(t, pk.SetHexString(refSk))

	//block, err := b.GetProposalData(context.Background(), 	1378551, pk)
	//require.NoError(t, err)

	// decode block
	block := &eth.BeaconBlock{}
	require.NoError(t, json.Unmarshal(_byteArray(refBlockByts), block))

	domain := &eth.DomainResponse{
		SignatureDomain:      _byteArray(refDomain),
	}
	signBlock, err := b.SignProposal(context.Background(), domain, block, pk)
	require.NoError(t, err)
	require.EqualValues(t, _byteArray(refSigByts), signBlock.Signature)

	//require.NoError(t, b.SubmitProposal(context.Background(), signBlock))
}

func _byteArray(input string) []byte {
	res, _ := hex.DecodeString(input)
	return res
}
